package com.janettha.jetpackcompose.themoviedb.data.repositoryimport com.janettha.jetpackcompose.themoviedb.core.data.Resourceimport com.janettha.jetpackcompose.themoviedb.data.datasource.web.api.MovieWebServiceimport com.janettha.jetpackcompose.themoviedb.data.datasource.web.model.MovieSectionResponseimport com.janettha.jetpackcompose.themoviedb.domain.repository.MovieRepositoryimport javax.inject.Injectclass MovieRepositoryImpl @Inject constructor(    private val remote: MovieWebService): MovieRepository {    private val mTag = "MovieRepositoryImpl"    override suspend fun getPopularMovieList(page: Int)    : Resource<MovieSectionResponse?> = executeApiRequestAsResource {        remote.getPopularMovieList(page = page)    }    override suspend fun getTopRatingMovieList(page: Int)            : Resource<MovieSectionResponse?> = executeApiRequestAsResource {        remote.getTopRatingMovieList(page = page)    }    override suspend fun getRecommendationMovieList(page: Int)            : Resource<MovieSectionResponse?> = executeApiRequestAsResource {        remote.getRecommendationMovieList(page = page)    }}